xpath.script:

document.addEventListener('DOMContentLoaded', function() {
    // 1. Mobile Menu Toggle
    const menuBtn = document.querySelector('.mobile-menu-btn');
    const navLinks = document.querySelector('.nav-links');
    if(menuBtn && navLinks) {
        menuBtn.addEventListener('click', function(e) {
            e.stopPropagation();
            navLinks.classList.toggle('active');
        });

        // Close menu when clicking outside
        document.addEventListener('click', function(e) {
            if (!navLinks.contains(e.target) && !menuBtn.contains(e.target)) {
                navLinks.classList.remove('active');
            }
        });
    }

    // 2. User Dropdown Toggle (Click-based for all devices)
    const userToggles = document.querySelectorAll('.user-toggle');
    userToggles.forEach(toggle => {
        toggle.addEventListener('click', function(e) {
            e.stopPropagation();
            const userMenu = this.parentElement.querySelector('.user-menu');
            const isOpen = userMenu.classList.contains('open');
            // Close all other user menus
            document.querySelectorAll('.user-menu').forEach(menu => menu.classList.remove('open'));
            // Toggle current
            if (!isOpen) {
                userMenu.classList.add('open');
            }
        });
    });

    // Close user menu when clicking outside
    document.addEventListener('click', function(e) {
        if (!e.target.closest('.user-dropdown')) {
            document.querySelectorAll('.user-menu').forEach(menu => menu.classList.remove('open'));
        }
    });

    // 3. Smooth Scrolling
    document.querySelectorAll('a[href*="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            const href = this.getAttribute('href');
            if (href.includes('acucare-home') || href.startsWith('#')) {
                const targetId = href.split('#')[1];
                const targetElement = document.getElementById(targetId);
                if (targetElement) {
                    e.preventDefault();
                    window.scrollTo({
                        top: targetElement.offsetTop - 80,
                        behavior: 'smooth'
                    });
                    if(navLinks) navLinks.classList.remove('active');
                }
            }
        });
    });

    // 4. Tab Functionality
    const tabBtns = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');
    if(tabBtns.length > 0) {
        tabBtns.forEach(btn => {
            btn.addEventListener('click', () => {
                tabBtns.forEach(b => b.classList.remove('active'));
                tabContents.forEach(c => c.classList.remove('active'));
                btn.classList.add('active');
                const tabId = btn.getAttribute('data-tab');
                const content = document.getElementById(tabId);
                if(content) content.classList.add('active');
            });
        });
    }

    // 5. Sticky Header Shadow
    window.addEventListener('scroll', function() {
        const header = document.querySelector('header');
        if(header) {
            if (window.scrollY > 100) {
                header.style.boxShadow = '0 5px 20px rgba(0, 0, 0, 0.1)';
            } else {
                header.style.boxShadow = '0 2px 10px rgba(0, 0, 0, 0.08)';
            }
        }
    });

    // 6. Touch device support for tabs and dropdowns (ensures click works on touch)
    if ('ontouchstart' in window) {
        // Already handled by click events, but ensure no double-tap zoom
        document.addEventListener('touchstart', function() {}, true);
    }
});
